import ContextQ1Detail2 from "./Detail2";
import React, { useContext } from "react";
import ModalContext from "../../store/2_context"; // 컨텍스트 import

const ContextQ1Detail = () => {
  const { isModalOpen, setIsModalOpen } = useContext(ModalContext); // 컨텍스트 사용

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div>
      <button onClick={toggleModal}>{isModalOpen ? "숨기기" : "보이기"}</button>
      {/* 나머지 컨텐츠 */}
      <ContextQ1Detail2 />
    </div>
  );
};

export default ContextQ1Detail;
